         private void _viewer_KeyDown(object sender, KeyEventArgs e)
         {
            const double zoomRatio = 1.2;
            double scaleFactor = _viewer.ScaleFactor;

            switch (e.KeyCode)
            {
               case Keys.Add:
               case Keys.Oemplus:
                  // Zoon in
                  scaleFactor *= zoomRatio;
                  e.Handled = true;
                  break;

               case Keys.Subtract:
               case Keys.OemMinus:
                  // Zone out
                  scaleFactor /= zoomRatio;
                  e.Handled = true;
                  break;

               case Keys.Enter:
                  scaleFactor = 1;
                  e.Handled = true;
                  break;
            }

