         Windows.Storage.Streams.IBuffer dib;
         loadFile = await Tools.AppLocalFolder.GetFileAsync(bmpFileName);
         using (RasterImage image = await codecs.LoadAsync(LeadStreamFactory.Create(loadFile)))
