// Remember, the index is zero-based, so 4th zone has an index of 3
OcrZone zone = ocrPage.Zones[3];
// Prevent this zone from being recognized
zone.ZoneType = OcrZoneType.Graphic;
ocrPage.Zones[3] = zone;

