///This method will comapre form with Extended MasterForms (conatins Table Form Field).
public FormRecognitionResult CompareExtendedForm(RasterImage page, FormRecognitionAttributes formAttributes, FormRecognitionAttributes masterAttributes, FormRecognitionEngine engine)
{
   //Note that mastersAttributes is attributes for master form that contains Table Form Field. 
   return engine.CompareExtendedForm(mastersAttributes, formAttributes, null);      
}

using Leadtools;
using Leadtools.Barcode;
using Leadtools.Codecs;
using Leadtools.Forms;
using Leadtools.Forms.Ocr;
using Leadtools.Forms.Processing;
using Leadtools.Forms.Recognition;
using Leadtools.Forms.Recognition.Barcode;
using Leadtools.Forms.Recognition.Ocr;

///This method will comapre form Master Form and if they matched then update Form FormPages.
public void FillFieldsInformation(FormPages pages, FormRecognitionAttributes formAttributes, FormRecognitionAttributes masterAttributes, FormRecognitionEngine engine)
{
   FormRecognitionResult result = engine.CompareForm(masterAttributes, formAttributes, null);
   //check if we have good results then update pages to used in Process stage
   if (result != null && result.Reason == FormRecognitionReason.Success && result.Confidence > 50)
   {
      //save all pages alignment in List and pass them to FillFieldsInformation
      List<PageAlignment> alignments = new List<PageAlignment>();
      for (int i = 0; i < result.PageResults.Count; i++)
         alignments.Add(result.PageResults[i].Alignment);
      //the pages will be updated
      engine.FillFieldsInformation(masterAttributes, formAttributes, pages, alignments);
   }
}

using Leadtools;
using Leadtools.Barcode;
using Leadtools.Codecs;
using Leadtools.Forms;
using Leadtools.Forms.Ocr;
using Leadtools.Forms.Processing;
using Leadtools.Forms.Recognition;
using Leadtools.Forms.Recognition.Barcode;
using Leadtools.Forms.Recognition.Ocr;

///This method calculates the aligmnet for the recognized form.
public void AlignForm(FormRecognitionEngine recognitionEngine, MyForm form, bool calculateAlignment)
{
   if(calculateAlignment)
   {
      form.Alignment = recognitionEngine.GetFormAlignment(form.Master.Attributes, form.Attributes, null);
   }
   else
   {
      form.Alignment = new List<PageAlignment>();
      for(int i = 0; i < form.Result.PageResults.Count; i++)
         form.Alignment.Add(form.Result.PageResults[i].Alignment);
   }
}

