using Leadtools.Services;
using leadtools.services.raster.datacontracts._2009._01;
using leadtools.services.datacontracts._2009._01;

public void RasterConvertOptionsExample()
{
   RasterServiceClient client = new RasterServiceClient();
   RawBinaryData sourceBinaryData = new RawBinaryData();
   sourceBinaryData.Data = File.ReadAllBytes(Path.Combine(LEAD_VARS.ImagesDir,"image1.cmp"));

   RasterConvertOptions convertOptions = new RasterConvertOptions();

   convertOptions.Source = sourceBinaryData;
   convertOptions.Destination = null;
   convertOptions.Format = RasterImageFormat.RasPdf;
   convertOptions.FirstPage = 1;
   convertOptions.LastPage = 1;
   convertOptions.BitsPerPixel = 24;
   convertOptions.QualityFactor = 2;
   convertOptions.BackColor = "Black";
   convertOptions.Height = 200;
   convertOptions.Width = 200;
   convertOptions.ResizeFlags =  RasterResizeFlags.Bicubic;
   convertOptions.ResizeMode = RasterResizeMode.Fit;
   convertOptions.HorizontalAlignMode = RasterAlignMode.Center;
   convertOptions.VerticalAlignMode = RasterAlignMode.Center;

   ConvertRequest request = new ConvertRequest();

   request.ConvertOptions = convertOptions;

   ConvertResponse response = client.Convert(request);
   if (response.Destination != null)
   {
      if(response.Destination is RawBinaryData)
         File.WriteAllBytes(Path.Combine(LEAD_VARS.ImagesDir,"converted_resized.pdf"), (response.Destination as RawBinaryData).Data);
   }

   client.Close();
}

